import {ADD_BOOKS, DELETE_BOOK} from '../constants'

export const addBook = data =>{
    return{
        type:ADD_BOOKS,
        payload:data
    }
}

export const deleteBook = ID =>{
    return{
        type:DELETE_BOOK,
        payload:id
    }
}
